					INSERT INTO access_role
					SELECT id,pIdRol
					FROM access
					WHERE id = pAux;
                END IF;
				INSERT INTO access_role
				SELECT id,pIdRol
				FROM access
				WHERE id = pIdAccess;
			END IF;
