				FROM (
						SELECT * 
						FROM access_role 
						WHERE 
							id_role = pIdRol AND 
							id_access IN(
								SELECT id 
								FROM access 
								WHERE parent = pIdParent)
						UNION
						SELECT * 
						FROM access_role 
						WHERE 
							id_role = pIdRol AND 
